import phones from 'api/mockPhones'
import categories from 'api/mockCategories'
import * as R from 'ramda'

export const fetchPhones = async () => {
    return new Promise((resolve, reject) => {
        resolve(phones)
    })
}

export const loadMore = async ({offset}) => {
    /* fetch.get(`http:xxx.com/api/phones?offset=${offset}`)
        for loading new(real, not mock) items on webpage */
    return new Promise((resolve, reject) => {
        resolve(phones)
    })
}

export const fetchPhoneById = async (id) => {
    return new Promise((resolve, reject) => {
        const phone = R.find(R.propEq('id', id), phones)
        resolve(phone)
    })
}

export const fetchCategories = async () => {
    return new Promise(resolve => {
      resolve(categories)
    })
  }
