import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import {connect} from 'react-redux'

import {getTotalBasketCount, getTotalBasketPrice} from 'selectors'

const BasketCart = ({totalBasketCount, totalPrice}) => {
    return (
        <div className="cart">
            <div className="dropdown">
                <Link 
                    to='/basket' 
                    id='dLabel' 
                    className='btn btn-primary btn-lg btn-block btn-color'
                >
                    <b>You currently have</b>
                   
                    <b> {totalBasketCount} items â€” ${totalPrice} </b>
                    <img src="/images/full-cart-16.png" alt="cart"></img>

                </Link>

            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        totalBasketCount : getTotalBasketCount(state),
        totalPrice : getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps, null)(BasketCart)