import React from "react";
import { connect } from "react-redux";
import * as R from 'ramda'
import { Link } from "react-router-dom";

import {
    getBasketItemsCount, getTotalBasketPrice
} from 'selectors'
import { 
    removeItemFromBasket, cleanBasket, basketCheckout
} from 'actions'

const Basket = ({
    phones, 
    totalPrice, 
    removeItemFromBasket,
    cleanBasket,
    basketCheckout
}) => {
    const isBasketEmpty = R.isEmpty(phones)
    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>Your shopping cart is empty</div>}
            <div className="table-responsive">
                <table className="table-bordered table-striped table-condensed cf">
                    <tbody>
                        {phones.map((phone, index) => (
                            <tr 
                                key={index}
                                className="item-checout"
                            >
                                <td className="first-column-checkout">
                                    <img 
                                        className="img-thumbnail" 
                                        src={phone.image} 
                                        alt={phone.name}></img>
                                </td>
                                <td>{phone.name}</td>
                                <td>${phone.price}</td>
                                <td>{phone.count}</td>
                                <td>
                                    <span 
                                        className="delete-cart"
                                        onClick={() => removeItemFromBasket(phone.id)}
                                    ></span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {R.not(isBasketEmpty) &&
                <div className="row d-flex justify-content-end">
                    <div className=" total-user-checkout">
                        <b>Total:</b>
                        ${totalPrice}
                    </div>
                </div>
            }
        </div>
    )
    const renderSidebar = () => (
        <div>
            <Link 
                to='/'
                className='btn btn-info'
            >
                <span>
                    <img src="images/info-16.png" alt="info"></img>
                </span>
                <span>Continue shopping!</span>
            </Link>
            {R.not(isBasketEmpty) &&
                <div>
                    <button
                        onClick={cleanBasket}
                        className="btn btn-danger"
                    >
                        <span>
                            <img src="images/trash-ww-16.png" alt="trash"></img>
                            Clean cart
                        </span>
                    </button>
                    <button
                        className="btn btn-success"
                        onClick={() => basketCheckout(phones)}
                    >
                        <span>
                            <img src="images/cart-16.png" alt="check"></img>
                        </span>
                        <span>
                            Checkout
                        </span>
                    </button>
                </div>
            }
        </div>
    )
    return (
        <div className="view-container">
            <div className="container">
                <div className="row">
                    <div className="col-md-9">
                        {renderContent()}
                    </div>
                    <div className="col-md-3 btn-user-checkout">
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        phones: getBasketItemsCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

const mapDispatchToProps = {
    removeItemFromBasket,
    cleanBasket,
    basketCheckout
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)