import * as R from 'ramda'

import {ADD_TO_BASKET, REMOVE_FROM_BASKET, CLEAN_BASKET} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_TO_BASKET:
            return R.append(payload, state)
        case REMOVE_FROM_BASKET:
            return R.without([payload], state)
        case CLEAN_BASKET:
            return initialState
        default:
            return state
    }
}